mapper.update:
UPDATE dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
WHERE id = #{id}

mapper.select:
SELECT d.id, d.name, d.category_id, d.price, d.image, d.description, d.status, d.update_time, c.name AS categoryName FROM dish d LEFT JOIN category c ON d.category_id = c.id
AND d.name LIKE CONCAT('%', #{name}, '%')
AND d.category_id = #{categoryId}
AND d.status = #{status}
ORDER BY d.create_time DESC
